var b, r, sm = 35,
    sc = ["#DDD", "#EEE"],
    se = "&#x2022;",
    ss = .75,
    sms = 8,
    ssi = 24,
    sr = 50,
    sst = "cursor:default;-webkit-user-select:none;-moz-user-select:none;-ms-user-select: none;-o-user-select:none;user-select:none;",
    snow = [],
    pos = [],
    coords = [],
    lefr = [];

function randomise(s) {
    return rand = Math.floor(s * Math.random()), rand
}

function init() {
    var s = ssi - sms;
    for (b = document.body.scrollHeight - 5, r = document.body.clientWidth - 15, i = 0; i <= sm; i++) coords[i] = 0, lefr[i] = 15 * Math.random(), pos[i] = .03 + Math.random() / 10, snow[i] = document.getElementById("flake" + i), snow[i].style.fontFamily = "inherit", snow[i].size = randomise(s) + sms, snow[i].style.fontSize = snow[i].size + "px", snow[i].style.color = sc[randomise(sc.length)], snow[i].style.zIndex = 1e3, snow[i].sink = ss * snow[i].size / 5, snow[i].posX = randomise(r - snow[i].size), snow[i].posY = randomise(2 * b - b - 2 * snow[i].size), snow[i].style.left = snow[i].posX + "px", snow[i].style.top = snow[i].posY + "px";
    mS()
}

function rs() {
    b = document.body.scrollHeight - 5, r = document.body.clientWidth - 15
}

function mS() {
    for (i = 0; i <= sm; i++) coords[i] += pos[i], snow[i].posY += snow[i].sink, snow[i].style.left = snow[i].posX + lefr[i] * Math.sin(coords[i]) + "px", snow[i].style.top = snow[i].posY + "px", (snow[i].posY >= b - 2 * snow[i].size || parseInt(snow[i].style.left) > r - 3 * lefr[i]) && (snow[i].posX = randomise(r - snow[i].size), snow[i].posY = 0);
    setTimeout("mS()", sr)
}
for (i = 0; i <= sm; i++) document.write(`<span id="flake${i}" style="${sst}opacity:50%;position:absolute;top:-${ssi}">${se}</span>`);
window.addEventListener("resize", rs), window.addEventListener("load", init);